    private void drawImage(Bitmap bitmap,Canvas canvas,Paint paint,int left,int top){
        //Bitmap bitmap = BitmapFactory.decodeResource(getResources(),R.drawable.photo_2020_love);
        int width = bitmap.getWidth()/2;
        int height = bitmap.getHeight()/2;
        Bitmap bmp = Bitmap.createBitmap(width,height, Bitmap.Config.ARGB_8888);
        RectF rectf = new RectF(0, 0, width, height);
        Canvas c = new Canvas(bmp);
        Path path = new Path();
        path.addRect(rectf, Path.Direction.CW);
        c.clipPath(path);
        c.drawBitmap( bitmap, new Rect(0, 0, bitmap.getWidth(), bitmap.getHeight()),
                new Rect(0, 0, width, height), paint);
        Matrix matrix = new Matrix();
        matrix.postScale(1f, 1f);
        Bitmap resizedBitmap = Bitmap.createBitmap(bmp, 0, 0, width, height, matrix, true);
        canvas.drawBitmap(resizedBitmap,left,top,paint);
    }
