    private ArrayList<Purpose>getIsCompletedToday(String isCompletedList){
        Observable<ArrayList<Purpose>> observable = Observable.just(PurposeLab.getInstance(MainActivity.this).readPurposesCompleted(isCompletedList));
        Disposable disposable = observable.subscribeWith(new DisposableObserver<ArrayList<Purpose>>() {
            @Override
            public void onNext(ArrayList<Purpose> purposes) {
                mIsCompletedPurposes = purposes;
            }

            @Override
            public void onError(Throwable e) {

            }

            @Override
            public void onComplete() {

            }
        });
        disposable.dispose();
        return mIsCompletedPurposes;
    }
